// Function to generate a random number based on tile position
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 truchet(vec2 uv, vec3 col, vec3 colIn)
{    vec2 pos = uv;
    vec2 tile = floor(uv);
    pos = fract(uv) ;
    // Generate a random number to choose 
    float choice = random(tile);
    float dist = 0.0;
    float nopc=1.0;
     float choice2 = random(tile);
    float ang = (choice2 < 0.5) ? radians(90.0) : radians(-90.0);
    pos = rotate(pos - 0.5, ang) + 0.5; // RotaciÃ³n con el centro del tile como origen

   col=Fig01(pos,col,colIn); 

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale =2.0;
    vec2 uv =  scale * ( 2. * fragCoord - iResolution.xy ) / iResolution.y ;
    uv+=iTime*0.25;
     vec3 col=vec3(1.0);
    col= truchet(uv,col, vec3(0.0,0.0,0.0));
      col=pow(col,  vec3(0.554545));  
    fragColor = vec4(col,1.0);
}
