vec3 palette( float t ) {
    vec3 a = vec3(0.838, 0.218, 0.888);
    vec3 b = vec3(0.748, 0.028, 0.588);
    vec3 c = vec3(0.888, 0.338, 0.838);
    vec3 d = vec3(1.418, 1.088, -0.862);

    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 paletteC( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}


float dot2( in vec2 v ) { return dot(v,v); }

float sdHeart( in vec2 p )
{
    p.x = abs(p.x);

    if( p.y+p.x>1.0 )
        return sqrt(dot2(p-vec2(0.25,0.75))) - sqrt(2.0)/4.0;
    return sqrt(min(dot2(p-vec2(0.00,1.00)),
                    dot2(p-0.5*max(p.x+p.y,0.0)))) * sign(p.x-p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    vec2 uvC = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uvC0 = uvC;
    vec3 circles = vec3(0.0);


    for( float i = 0.0; i< 1.0; i++){
        uv.y += 0.58;

        float d = sdHeart(uv)  * exp(-length(uv));
        vec3 col = palette(length(uv0) + i*6. + iTime*0.5);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);
        d = smoothstep(0.0,0.1,d);
        d = pow(0.3 / d, 1.5);



        finalColor += col * d ;
    }

    for( float i = 0.0; i< 2.0; i++){        
        //circles
        uvC = fract(uvC * 1.5) - 0.5;

        float dC = length(uvC) * exp(-length(uvC0));

        vec3 colC = paletteC(length(uvC0) + i*.4 + iTime*.5);

        dC = sin(dC*8. + iTime*0.5)/8.;
        dC = abs(dC);

        dC = pow(0.01 / dC, 1.5);

        circles += colC * dC;
        circles *= .5;


    }



    // Output to screen
    fragColor = vec4(circles+finalColor,1.0);
}
