const float EPSILON = 0.0001;
const float NEAR = 0.001;
const float FAR = 100.0;

float sdSphere(vec3 p, float r)
{
  return length(p) - r;
}

float sdBox(vec3 p, vec3 b)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float map(vec3 p)
{
    //float ground = p.y + 0.75;
    vec3 spherePosition = vec3(/*sin(iTime) * 3.0*/0.0, 0.0, 0.0);
    float sphere = sdSphere(p - spherePosition, 1.0);
    return sphere;
    //return min(sphere, ground);
}


vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        map(vec3(p.x + EPSILON, p.y, p.z)) - map(vec3(p.x - EPSILON, p.y, p.z)),
        map(vec3(p.x, p.y + EPSILON, p.z)) - map(vec3(p.x, p.y - EPSILON, p.z)),
        map(vec3(p.x, p.y, p.z  + EPSILON)) - map(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 phong(vec3 position, vec3 normal, vec3 cameraPosition,
    vec3 lightPosition, vec3 lightColor,
    float ambientIntensity, float diffuseIntensity, float specularPower) 
{
    // 1. Calculate lighting vectors
    vec3 N = normal; // Surface normal
    vec3 L = normalize(lightPosition - position); // Direction to light
    vec3 V = normalize(cameraPosition - position); // Direction to camera
    vec3 R = reflect(-L, N); // Reflection vector

    // 2. Calculate lighting vector similarities
    float dotNL = max(dot(N, L), 0.0); // Diffuse term
    float dotVR = pow(max(dot(V, R), 0.0), specularPower); // Specular term

    // 3. Apply ambient, diffuse, and specular contributions
    vec3 C = vec3(0.0); // Base color (black)
    C += lightColor * ambientIntensity; // Ambient contribution
    C += lightColor * diffuseIntensity * dotNL; // Diffuse contribution
    C += lightColor * dotVR; // Specular contribution

    return C; // Output color
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // [0, 1] --> [-1, 1]
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    // Ray origin
    vec3 ro = vec3(0.0, 0.0, -3.0);

    // FoV of 90 degrees
    float fov = tan(45.0 * 3.14 / 180.0);

    // Ray direction
    vec3 rd = normalize(vec3(uv * fov, 1.0));

    // Distance along ray
    float t = 0.0;

    for (int i = 0; i < 80; i++)
    {
        // Scene position
        vec3 p = ro + rd * t;

        // Determine maxmimum safe stepping distance
        float d = map(p);

        // Step along ray by distance d
        t += d;

        // Intersection if distance is very close to surface (SDF --> -1)
        if (d < NEAR) break;

        // No intersection if distance along ray exceeds view distance
        if (t > FAR) break;
    }

    vec3 p = ro + rd * t;
    vec3 n = estimateNormal(p);

    vec3 lightColor = n * 0.5 + 0.5;//vec3(1.0, 1.0, 1.0);
    vec3 lightPosition = vec3(sin(iTime) * 5.0, 5.0, -5.0);
    float ambient = 0.25;
    float diffuse = 0.75;
    float specular = 32.0;
    vec3 lighting = phong(p, n, ro, lightPosition, lightColor, ambient, diffuse, specular);

    bool hit = t < FAR - EPSILON;
    vec3 color = hit ? lighting : vec3(0.25, 0.25, 0.25);

    fragColor = vec4(color, 1.0);
}
