// the palette is good for dynamicity and creating patterns
// check http://dev.thi.ng/gradients/

vec3 palette( float t){
    vec3 a = vec3(0.500, 0.500, 0.500);
    vec3 b = vec3(1., 1., 1.);
    vec3 c = vec3(1., 1., 1.);
    vec3 d = vec3(0.000, 0.333, 0.667);

    return a + b*cos( 3.*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.x;

    uv = fract(uv * 5.);

    float d = length(uv);

    // I time gives more dynamic to the colors
    vec3 col = palette(d + iTime);


    d = sin(d * 16. - iTime)/8.;
    d = abs(d);

    d = 0.02/d;

    col *= d;

    fragColor = vec4(col, 1.0);
}
