// click mouse on canvas to change speed & "fluidity"
// mouse.x controls "fluidity"
// mouse.y controls speed

float ball(float x0,float y0,float a,float b,float aa,float bb,float rr){

     float t = (0.2+iMouse.y/iResolution.y)*iTime+(iMouse.x/iResolution.x)*(x0+y0);
    float x =  4.0*x0-2.0-a*cos(t*aa);
    float y =( 4.0*y0-1.0-b*sin(t*bb));
    float r =  (0.3+0.25*cos(rr*t))-4.0*(x*x+y*y);
    if(r<0.0)r=1.0/(0.1-r);
    else r=1.0/(0.9+abs(x0*x+y0*y));
    return 0.25*pow(r,0.5);



}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 u = fragCoord/iResolution.x;

    fragColor = vec4( 
        ball(u.x,u.y,   1.0,  1.,  3.0, 4.2,12.0)+
        ball(u.x,u.y,   1.2, -0.1,  3.0, 5.2,9.0)+
        ball(u.x,u.y,   1.4,  1.1,  2.0, 4.2,5.0)+
        ball(u.x,u.y, -1.2, 0.4, 5.0, 4.2,7.6),
        0.0,
        0.0
        ,1);
}
