#define ITERATIONS 18

vec3 lighten(vec3 c, float d) {
	return vec3(min(1.0, c.r + d), min(1.0, c.g + d), min(1.0, c.b + d));
}

vec3 pastel(vec3 c) {
	float avg = (c.r + c.g + c.b) / 3.0;
	return lighten(c, avg);
}

vec2 rotate2D(vec2 p, float angle) {
	return vec2(
        p.x * cos(angle) + p.y * sin(angle), 
        -p.x * sin(angle) + p.y * cos(angle)
    );
}

float julia(vec2 c,vec2 c1, float max){
	float xx = c.x * c.x;
	float yy = c.y * c.y;
	float xy = c.x * c.y;
	float s = xx + yy;

	for (int i = ITERATIONS; i > 0; i--) {
		c = vec2(xx - yy, xy + xy) + c1;
		xx = c.x * c.x;
		yy = c.y * c.y;
		xy = c.x * c.y;
		s = xx + yy;

		if (s >= max || i == 1) {
            return float(ITERATIONS - i) / float(ITERATIONS);
        }
	}
	return 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord / iResolution.xy;	
	vec2 juv  = rotate2D(uv - vec2(0.5), iTime * 0.74) + vec2(cos(iTime * 2.0) * 0.1, sin(iTime) * 0.1);

	float j = julia(juv, juv, 16.0);
	float r = j * sin(iTime + uv.y * 2000.0 * sin(iTime)) * cos((juv.x + juv.y) * iTime * 2.0)
                * sin(uv.y * sin(iTime)* 3.0);
	float g = j * cos(iTime + uv.y * 3000.0 * cos(iTime))
                * sin(juv.x * iTime * 6.0 + cos(juv.x + juv.y) * iTime * 333.0);

	float b = (r + g + sin(iTime * 6.0) *uv.y) / 2.0;

	fragColor = vec4(pastel(vec3(r, g, b)), 1.0);
}
