#define PI 3.14159265359
#define TPI 6.28318530718

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv.x -= 0.35;

    vec2 diff = uv - 0.5;

    float dist = sqrt(dot(diff,diff));


    float angle = PI;
    mat2 rot = mat2(cos(angle),sin(angle),-sin(angle),cos(angle));
    vec2 p = -diff;
    float L = length(p);
    float f = 0.;

    float padding = 0.15;

    float padX = 0.02;
    float rng = 1. - 0.5 - padding - padX;// step(0.5 + padding + padX,pow(1.-dist, 1.));
    rng = smoothstep(L-.005, L, rng);

    float thickness = 0.01/2.;
    float circle = step(0.5 + padding - thickness / 2., 1.-dist) 
    * step(1.-dist, 0.5 + padding + thickness / 2.);

    f = smoothstep(L-.005, L, 1. - 0.5 - padding);

    float percent = mod(iTime, 1.0);
    float t2 = TPI * (percent - 0.5); //-PI to PI

    float a = atan(p.x,p.y);
    f = f * step(a,t2);

    vec3 baseCol = vec3(1.,1.,0.);
    vec3 circleCol = vec3(0.,1.,0.);
    vec3 stateCol = vec3(1.,0.,0.);

    vec3 fCol = mix(circleCol * circle, stateCol * f, f)  + baseCol * rng;

    // Output to screen
    fragColor = vec4(fCol,1.0);
}
