#define EYES 100.

float Eye(vec2 p, vec2 offs, float s1, float s2, float size){
    float c1 = smoothstep(s1, s1-.01, length(size*p));
    //s2 *= sin(iTime)*.4+.8;
    float c2 = smoothstep(s2, s2-.01, length(size*p-offs));
    c1 *= .05/length(p);  // for brightness
    //another funky effect
    // c1 *= .02/(sin(length(p.x))-sin(length(p.y)*.5));
    return c1-c2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 1.5;
    vec3 col = vec3(0);
    float a = smoothstep(.37, .36, length(uv))-smoothstep(.28, .27, length(uv-vec2(.1, -.05)));
    float fade = sin(uv.x+.5)*.5+.5;
    //col += a;//*fade;


    /* // Googly Eyes    
    vec2 p1 = vec2(-.33, 0.2);
    vec2 p2 = vec2(.33, 0.2);
    float eyeL = Eye(uv-p1, vec2(.1, -.1), .6, .4, 3.);
    float eyeR = Eye(uv-p2, vec2(-.1, -.1), .6, .4, 3.);
    */

    for(float i=0.; i<1.; i+=1./EYES){
        float eye = Eye(uv-vec2(2.*i-1.2, i*sin(iTime*i)/2.), vec2(-.1,.1), .6, .4, 5./i);
        vec3 color = sin(.2*iTime*vec3(3., 5., 2.)*i)*.5+.5;
        col += eye * color;
    }
    // Bouncing-randomly-around-the-room eye? 
    //vec2 height = vec2(sin(iTime), sin(iTime*2.)*smoothstep(1.5, 0., -uv.y));
    //vec2 offs = vec2(.2*sin(iTime+21.41), .2*cos(iTime));
    //float eye = Eye(uv-height, offs, .6, .4, 3.);
    //col += eye;

    fragColor = vec4(col,1.0);
}
