void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x/iResolution.y; 
    uv *= .2;
    uv.x -=1.7;
    uv.y +=0.05;
    vec3 col;

    vec2 z = uv;
    vec2 c = z;
    int iterations = 20;
    for(int i =0;i<iterations;i++){
    float I = float(i);
        z = vec2(abs(z.x * z.x - z.y * z.y), -abs(2. * z.x * z.y)) + c;

         if(length(z) < 2.) i >=
         iterations-1 ? col = vec3(0,0,0)
         : col = vec3( cos(I + sin(I + sin(iTime))),  sin(I), atan(sin(I)/cos(I)));
    }


    fragColor = vec4(col,1.0);
}
