// ---
// from https://www.shadertoy.com/view/wt23Rt

#define saturate(v) clamp(v,0.,1.)

vec3 hsv2rgb(vec3 c){
	vec4 K=vec4(1.,2./3.,1./3.,3.);
	return c.z*mix(K.xxx,saturate(abs(fract(c.x+K.xyz)*6.-K.w)-K.x),c.y);
}

// ---

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.rgb = hsv2rgb(texture(iChannel0, fragCoord/iResolution.xy).rgb);
}

vec3 hash23(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame == 0)
    {
        fragColor.rgb = hash23(floor(fragCoord / 5.0)*5.0);
    }
    else
    {
        vec3 col = texture(iChannel0, floor(fragCoord / 5.0)*5.0/iResolution.xy).rgb;
        float matte = step(dot(col, vec3(-1, 1, 0)), 0.5);

        fragColor.rgb = texture(iChannel1, fragCoord/iResolution.xy).rgb + vec3(2.0, 0.0, 0.0) * iTimeDelta * matte;
    }
}
