#define r(a) mat2(cos(a - vec4(0,11,33,0)))

void mainImage(out vec4 o, vec2 u) 
{
    float i, 
          k,
          s, 
          t = iTime,
          d = 1.;

    vec3 R = iResolution,
         a,
         b = normalize(vec3(u+u-R.xy, 1.3*R.y)),
         p;

    a.x = t/55.;
    b.xz *= r(-1.5);
    b.yz *= r(t/45.);

    // Foor loop
    for (o *= i; i++ < 40. && d > 1e-5; k += d / 3.5) 
        // Magic fractal stuff
        for (d = 0., s = 1.; s < 3e2;) 
            p = a + k * b,
            p.yz *= r(4.*s),
            p.xz *= r(s*s),
            p = abs(fract(s*p) - .5),
            s *= 2. + .1*fract(.1*floor(t/2.)), // was s+=s
            p = abs(p),
            d += ( max(p.x, max(p.y,p.z)) - .35 + .2*cos(s) ) / s,
            o += (d+d*cos(d*1e2))
               * (1. + cos(vec4(3,4,5,0) + s*s/4.));

    // Magic tonemapping
    o = o/4. + 110./i/i;
    o -= o*exp(-4.*k);
    o = mix(o, 1.-1./cosh(8.*o*o), .43);
    o = pow(o, .65 + o-o);
    o = mix(o, tanh(1.4*o), .4);    
}
