void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 2.0;
    vec2 uvv = atan(fragCoord/iResolution.xy * 50. - 50./2.) * 50.;



    vec2 col = 0.5 + 0.5*cos(iTime+uvv.xy+vec2(tan(iTime * speed),cos(iTime * speed)));
    col.x = col.x + atanh(pow(col.x * 1.1,iTime));
    col.y = col.y + atanh(pow(col.y * 1.1,iTime));

    float RAHHH = 0.0;
    float rotation = 0.0;
    float numba = 0.1;
    float NUMBDER = -3.0;
    vec2 uv = vec2(fragCoord.y - iResolution.y/2.0, fragCoord.x - iResolution.x/2.0);
    rotation = atan(uv.y/uv.x)+3.14*float(bool(uv.x<0.));
    NUMBDER -= fract(iTime);
    float power = distance(vec2(0,0), uv.xy);
    for(int j=0; j<28; j++){
        if ((rotation < NUMBDER + 0.2 + numba + power/200.) && (rotation > NUMBDER + numba + power/200.)) {RAHHH = 1.0;}
        numba += 0.448;
    }
    fragColor = vec4(col, RAHHH,0.0);

}
