float xorshift32(int x) {
    x ^= x << 13;
    x ^= x >> 17;
    x ^= x << 5;
    return intBitsToFloat(
        0x3F800000 | (
            0x007FFFFF & x
        )
    ) - 1.0;
}

const float TURN = acos(-1.) * 2.;
float zag(float x, float border) {
    float w = fwidth(x) * 0.75;
    return step(
        border,
        smoothstep(
            0.5 - w, 0.5 + w,
            1. - abs(1. - 2. * fract(x))
        )
    );
}

#define HEX(x) (vec3(uvec3(255) & (uvec3(x) >> uvec3(16, 8, 0))) / 255.)
vec3 colormap(float hue) {
    const vec3[] cols = vec3[](
    HEX(0xffffff),
    HEX(0x00cbf8),
    HEX(0xfb20d2),
    HEX(0xf96b07),
    HEX(0xffef00),
    HEX(0x07e92d),
    HEX(0x010a31),
    HEX(0x540abd)
    );

    return mix(
        cols[int(hue) & 7],
        cols[int(hue + 1.) & 7],
        fract(hue)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime / 16.);
    vec2 uv = (2. * fragCoord - iResolution.xy) / length(iResolution);

    float r = length(uv);

    const float HSPEED = 8.;
    float h = ceil(
        5.5 * sqrt(
            length(dot(
                uv,
                vec2(sin(t * TURN / 2.), cos(t * TURN / 2.))
            ))
        )
    + HSPEED * t) - HSPEED * t;
    float fh = 1. - h;
    fh *= fh;
    fh *= fh;
    fh = 1. - h;

    float stripes = zag(
        8.5 * r / (fh + 0.25)
        + 12. * t,
        xorshift32(
            floatBitsToInt(exp(uv.x * 0.3 + uv.y)) +
            iFrame
        )
    );

    vec3 col = colormap(
        //float(iFrame >> 1) +
        3. * (h + HSPEED * t) +
        1. * stripes
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
