void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
     vec2 uv = (fragCoord / iResolution.xy) * 2. - 1.;
     vec2 uvb = uv;

     //fix aspect ratio.
     uv.x *= iResolution.x / iResolution.y;

     //uv *= 2.;
     //uv = fract(uv);
     //uv -= .5;

     vec3 color = .5 + .5 * cos(iTime + uv.xyx + vec3(0, 1, 2) * length(uvb) + iTime);

     float dist = uv.x / uv.y;
     dist = sin(dist * 8. + iTime) / 8.;
     dist = abs(dist);
     //dist = smoothstep(0., .1, dist);

     dist = .02 / dist;

     color *= dist;

     fragColor = vec4(color, 1.);
}
