void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 buff1 = texture(iChannel0, uv);
    vec4 buff2 = texture(iChannel1, uv);
    vec4 buff3 = texture(iChannel2, uv);
    vec3 c = buff1.rgb + buff2.rgb + buff3.rgb;

    fragColor = vec4(c, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float ixy = iResolution.x / iResolution.y;

    vec3 col = vec3(1.,0.,0.);

    float c = 1. - smoothstep(0., 0.01, length(uv-vec2(0.5 * ixy, 0.5)) - 0.1);

    fragColor = vec4(col * c,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float ixy = iResolution.x / iResolution.y;

    vec3 col = vec3(0.,1.,0.);

    vec2 center = vec2((sin(iTime) * 0.5 + 0.5) * ixy, 0.5);

    float c = 1. - smoothstep(0., 0.01, length(uv - center) - 0.1);

    fragColor = vec4(col * c,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float ixy = iResolution.x / iResolution.y;


    vec2 center = vec2((sin(iTime) * 0.5 + 0.5) * ixy, cos(iTime) * 0.5 + 0.5);

    vec2 uv2 = fragCoord / iResolution.xy;
    float ghosting = sin(iTime);
    vec3 fin_c = texture(iChannel0, uv2).rgb * ghosting;


    float c = 1. - smoothstep(0., 0.01, length(uv - center) - 0.1);

    vec3 circle = vec3(0.,0,1.) * c;
    fin_c += circle;

    fragColor = vec4(fin_c,1.0);
}
