void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    uv -= .5;
    uv *= iResolution.x / iResolution.y;

    float dist = length(uv);

    vec3 left = sin(vec3(0.0, 1.0, 0.0));
    vec3 right = sin(vec3(0.0, 1.0, 1.0));
    vec3 grad1 = mix(left, right, uv.x +1.45);

    vec3 top = vec3(.75, 0.5, 0.0);
    vec3 bottom = vec3(1.0, 1.0, 0.0);
    vec3 grad2 = mix(top, bottom, uv.y+.75);

    vec3 col = vec3(step(sin(dist * 15.0 + (2.45 * iTime)), 0.4));

    col *= mix(grad1, grad2, uv.x + 0.5);
    fragColor = vec4(col, 1.0);

}
