vec2 distort(vec2 uv, float time) {
    float repeat = 4.0;
    float speed = 4.0;
    float power = 0.3;

    float effect = (sin(repeat * 3.14 * uv.y + time * speed) + 1.0) * 0.5;
    float effect2 = sin(repeat * 3.14 * uv.y + time * speed) * 0.1;

    vec2 offset = vec2((0.5 - uv.x) * effect, effect2);

    return uv + offset * power;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
	uv = distort(uv, iTime);
	fragColor = vec4(texture(iChannel0, uv) );
}
