void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime *50.;



    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float varX=0.5+sin(t*uv.x)*10.;

    varX=step(.1,varX);

    // Time varying pixel color

    vec2 col = 0.5 + 0.5*cos(iTime+uv.y+vec2(0,-2));


    // Output to screen
    fragColor = vec4(varX,0,col.y*0.1,1.);
}
