float DFS_Center(vec3 pos){
float dist = length(pos) + 0.8;
float dist2 = length(pos) + 0.3;


return pow(dist, 20.0) + pow(dist2, 20.0);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.0 + sin(float(0.1)*iTime)*0.5*cos(iTime+uv.xyx+vec3(0,2,4)) * DFS_Center(uv.xyx - vec3(0.5));

    // Output to screen
    fragColor = vec4(col,1.0);
}
