void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime;

    // 3D Perspective Road
    float perspective = 1.5 / (uv.y + 0.7);
    float roadCurvature = 0.2 * sin(time * 10.0 + uv.y * 5.0); // Curved road
    float roadX = uv.x * perspective + roadCurvature;
    float roadEdge = smoothstep(0.3 * perspective, 0.3 * perspective + 0.02, abs(roadX));
    float rainbow = sin(10.0 * uv.y + time * 5.0) * 0.5 + 0.5;
    vec3 roadColor = mix(vec3(0.0), vec3(rainbow, uv.y, 1.0 - uv.y), 1.0 - roadEdge);

    // Rival Karts
    float rivalCount = 3.0;
    vec3 rivalColor = vec3(0.0, 1.0, 1.0);
    for (float i = 0.0; i < rivalCount; i++) {
        float rivalY = fract(time * 0.3 + i * 0.33);
        float rivalX = 0.1 * sin(time * 3.0 + i * 3.1415);
        float rival = smoothstep(0.02, 0.0, length(uv - vec2(rivalX, rivalY)));
        roadColor += rival * rivalColor;
    }

    // Speed Lines
    float lineSpeed = fract(uv.y + time * 2.0);
    float speedLine = smoothstep(0.01, 0.0, abs(uv.x - lineSpeed * 2.0));
    roadColor += vec3(0.1, 0.1, 0.3) * speedLine;

    // Cosmic Explosion Background
    float explosion = sin(uv.x * 20.0 + time * 3.0) * sin(uv.y * 20.0 + time * 3.0);
    explosion = pow(explosion, 2.0); // Amplify the effect
    vec3 bgColor = vec3(0.2, 0.1, 0.5) + explosion * vec3(1.0, 0.5, 0.2);

    // BPM Sync (175 BPM)
    float bpm = 175.0 / 60.0;
    float flash = step(0.9, sin(6.2831 * bpm * time * 2.0)); // Faster flashes
    vec3 bpmFlash = vec3(flash) * 0.05; // Less bright


    // Combine all layers
    vec3 finalColor = bgColor + roadColor + bpmFlash;
    fragColor = vec4(finalColor, 1.0);
}
