#define R3D(a,A) mix(A*dot(p,A), p, cos(a)) + sin(a)*cross(p,A)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=4.,e,t=iTime; vec3 p,q=vec3(-3,5,2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,1);    
    p*=R3D(t,-q);

    for(j=0.;j++<4.;)
        p=abs(p)*R3D(length(FC),q)-.04,
        d=min(max(p.y,p.z), p.x/d),
        d-=dot(p,q)/1e5;
    e-=d;
}o+=vec4(exp(p/d),1)/i;
}
