void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized and centered pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 col;
    float t = iTime*0.5;


    // Time varying pixel color
    col = (vec3(fract(length(uv)-t)))*
    (vec3(fract(distance(uv, vec2(-iResolution.x/iResolution.y, 0.0))-t)))*
    (vec3(fract(distance(uv, vec2(iResolution.x/iResolution.y, 0.0))-t)));//*
    //(vec3(fract(distance(uv, vec2(0.0, 1.0))-t)))*
    //(vec3(fract(distance(uv, vec2(0.0, -1.0))-t)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
