void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 uMouse = iMouse.xy/iResolution.y;

    float hline = smoothstep(uMouse.y-0.01,uMouse.y,uv.y) - smoothstep(uMouse.y,uMouse.y+0.01,uv.y);
    float vline = smoothstep(uMouse.x-0.01,uMouse.x,uv.x) - smoothstep(uMouse.x,uMouse.x+0.01,uv.x);
    vec3 color = vec3(hline,0,0) + vec3(vline,0,0);
    fragColor = vec4(color,1.0);
}
