float[] k = float[](
     1.0,-2.0, 1.0,
    -2.0,-4.0, 2.0,
     1.0, 2.0, 1.0
);
float multiplier = 1000.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 video = vec2(textureSize(iChannel0, 1)) * 2.;//courtesy kosalos
    vec2 scale = video / iResolution.xy;
    ivec2 coord = ivec2(fragCoord * scale);

    vec3[] cols = vec3[](
        texelFetch(iChannel0,coord+ivec2(1,-1),0).xyz,
        texelFetch(iChannel0,coord+ivec2(1, 0),0).xyz,
        texelFetch(iChannel0,coord+ivec2(1, 1),0).xyz,

        texelFetch(iChannel0,coord+ivec2(0,-1),0).xyz,
        texelFetch(iChannel0,coord+ivec2(0, 0),0).xyz,
        texelFetch(iChannel0,coord+ivec2(0, 1),0).xyz,

        texelFetch(iChannel0,coord+ivec2(-1,-1),0).xyz,
        texelFetch(iChannel0,coord+ivec2(-1, 0),0).xyz,
        texelFetch(iChannel0,coord+ivec2(-1, 1),0).xyz
    );

    vec3 col = vec3(0);
    for(int i = 0; i < 9; i++){
        col+=cols[i]*k[i];
    }

    fragColor = vec4(col*multiplier,1.0);
}
