//ref:https://www.shadertoy.com/view/cttBzl
#define time iTime
#define PI acos(-1.)
#define TAU (2. * PI)
#define VOL 0.
#define SOL 1.
#define saturate(x) clamp(x, 0., 1.)
#define mul_num exp(-15.)

float beat, beatTau;
vec3 pos;
float scene;

vec3 pal(float h) {
  vec3 col = vec3(0.5) + 0.5 * cos(TAU * (vec3(0, 0.33, 0.67) + h));
  return mix(col, vec3(1), 0.1 * floor(h));
}

float sdBox(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q, 0.)) + min(0., max(q.x, max(q.y, q.z)));
}

void rot(inout vec2 p, float a) { p *= mat2(cos(a), sin(a), -sin(a), cos(a)); }

void U(inout vec4 m, float d, float a, float b, float c) {
  if (d < m.x) m = vec4(d, a, b, c);
}

vec4 map(vec3 p) {
  pos = p;

  float a = 16.;
  vec4 m = vec4(1);
  vec3 of1 = vec3(6.26, 2.7, 1.5);
  p = mod(p, a) - 0.5 * a;
  p -= of1;

  for (int i = 0; i < 1 + int(scene); i++) {
    p = abs(p + of1) - of1;
    rot(p.xz, TAU * (0.05 + scene * 0.));
    rot(p.zy, TAU * 0.35);
    rot(p.xy, TAU * -0.05);
  }

  vec3 p2 = p;
  p2.y = mod(p2.y, 0.4) - 0.5 * 0.4;

  vec3 p3 = p;
  p3.y = mod(p3.y, 4.) - 0.5 * 4.;
  U(m, sdBox(p2, vec3(1, 0.05, 1)), SOL, 0.1, 1.);
  U(m, sdBox(p, vec3(0.5, 20, 0.5)), VOL, saturate(cos(beatTau / 2. + p.y * TAU / 4.)),
    2.8 + 0.1 * scene + ((scene == 3.) ? (fract(p.y * 2.3) - 2.) : 0.));
  U(m, sdBox(p3, vec3(1, 0.2, 1)), VOL, saturate(cos(beatTau / 2. + TAU / 32. * pos.z / 16.)),
    2.4 + 0.1 * scene);

  return m;
}

vec3 render(vec3 ro, vec3 rd) {
  vec3 col = vec3(0);
  float t = 0.;
  for (int i = 0; i < 100; i++) {
    vec3 p = ro + rd * t;
    vec4 m = map(p);
    float d = m.x;

    if (m.y == SOL) {
      t += d;
      if (d < t * 0.001) {
        col += float(i) / 50. * pal(m.w) * m.z;
        t += d;
        break;
      }
    } else {
      t += 0.1 * abs(d) + 0.1;
      col += 0.2 * pal(m.w) * m.z * exp(-3. * d);
    }
  }
  col = mix(vec3(0), col, exp(-0.001 * t));
  return col;
}
float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0 + 113.0*p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}
mat3 m = mat3( 0.00,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );
float fbm( vec3 p )
{
    float f;
    f  = 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p );
    return f;
}
float map2(vec3 p) {
    float m1 = length(map(p)) - 0.05;
    vec3 a = vec3(1.0);
    p = mod(p, a) - 0.5 * a;

        p = abs(p) - 0.5;
        rot(p.xy, 0.1);
        p = abs(p) - 0.3;
        rot(p.yz, 0.1);

    return max(m1, sdBox(p, vec3(1.1, 0.01, 0.01)));
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  beat = time * 120. / 60.;
  beatTau = beat * TAU;
  scene = floor(beat / 4.);
  scene += saturate(pow(fract(beat / 4.), 1.) - abs(uv.y));
  // vec2 up = vec2(0, 1);
  // rot(up, TAU * pow(fract(beat / 4), 3));
  // scene += dot(uv, up) > 0 ? 1 : 0;
  scene = mod(floor(scene + 0.5), 6.);

  vec3 ro = vec3(0, 0, 4. * time);
  vec3 rd = vec3(uv, 1);
  rd = normalize(rd);
  vec3 col = render(ro, rd);
      vec3 p = ro;
    bool hit = false;
    float t = 0.0;
    int i;
        vec3 target = ro + vec3(0., 0., 1.) + 0.01 * fbm(vec3(time * TAU * 2.));
    vec3 up = vec3(0, 1, 0);

    vec3 fwd = normalize(target - ro);
    vec3 right = normalize(cross(up, fwd));
    up = normalize(cross(fwd, right));
    vec3 ray = normalize(right * uv.x + up * uv.y + fwd * 0.5);
    float t2 = 0.0, ac = 0.0;

    for (i = 0; i < 100; i++) {
        p = ro + ray * t2;
        float d = map2(p);
        d = d;
        ac += exp(-10. * d);

        t2 += d * 0.5;
        if (t2 > t) break;
    }
  col = vec3(0.06, 0.3, 0.9) * ac/(render(ro, rd)*mul_num);
  fragColor = vec4(col, 1);
}
