float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float cerp(float a, float b, float t)
{
    return a*(2.*t*t*t-3.*t*t+1.)+b*(-2.*t*t*t+3.*t*t);
}

vec2 randVec(vec2 pos)
{
    float theta = 3.14159 * hash12(pos) + iTime;
    return vec2(cos(theta), sin(theta));
}
void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = 4. * (2. * I - iResolution.xy)/iResolution.y;
    vec3 col;
    float val;
    vec2 gridPos = fract(uv);
    vec2 gridCorner;
    vec2 cornerVec;
    vec2 offset;
    float cornerVal[4];
    for(uint i; i < 4u; i++)
    {
        offset = vec2(i & 1u, i >> 1u);
        //order of offset
        //[3][4]
        //[1][2]
        gridCorner = floor(uv) + offset;
        cornerVec = gridPos - offset;
        cornerVal[i] = dot(cornerVec, randVec(gridCorner));
    }
    val = cerp(cerp(cornerVal[0], cornerVal[1], gridPos.x), cerp(cornerVal[2], cornerVal[3], gridPos.x), gridPos.y);
    val = 0.5 * val + 0.5;
    col = vec3(val);
    O = vec4(col,1.0);
}
