#define rot(r) mat2(cos(r + vec4(0, 33, 11, 0)))

void mainImage(out vec4 o, vec2 u) {
    vec3 r = iResolution, p; 
    u = (u - r.xy / 2.) / r.y;

    float c, d, i, j, g, 
          e = 1., t = iTime * .5;

    o *= 0.; 
    while(i++ < 20.) {
        p = vec3(u * g, g - 5.);
        p.y += 1.7;
        p.z -= 1.;

        j = 0.; 
        while(j++ < 7.)
            c = j,
            d = length(max(abs(p) - vec3(.1, .6, .3), 0.)) 
                    * pow(.74, j),
            e = min(e, d),

            p.xz *= rot(cos(t * j * .3) * 2.),
            p.x = abs(.8 * p.x) - .6,
            p.y -= 1.,
            p.xy *= rot(1.2),
            p *= 1.3;

        g += e, 
        o.rg += 3.6e-4 / e - .045;
        o.b += 4e-4 / e - .05;
    }
    o *= o;
}
