/*
Doing more Raymarching! 

Credit to this website for the raymarching tutorial:
https://inspirnathan.com/posts/47-shadertoy-tutorial-part-1
Credit to Inigo Quilez for his 3D Primitives.
*/

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float PRECISION = 0.001;

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}
mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}
mat3 identity() {
    return mat3(
        vec3(1, 0, 0),
        vec3(0, 1, 0),
        vec3(0, 0, 1)
    );
}

struct Surface {
    float sd; // signed distance value
    vec3 col; // color
};
Surface sdSphere(vec3 p, float r, vec3 offset, vec3 col){
  float d = length(p - offset) - r;
  return Surface(d, col);
}
Surface sdBoxFrame(vec3 p, vec3 b, float e, vec3 col, mat3 transform){
  p = abs(p * transform) - b;
  vec3 q = abs(p+e) - e;
  float d = min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
  return Surface(d, col);
}
Surface sdFloor(vec3 p, vec3 col) {
  float d = p.y + 1.;
  return Surface(d, col);
}
Surface minWithColor(Surface obj1, Surface obj2) {
  if (obj2.sd < obj1.sd) return obj2; // The sd component of the struct holds the "signed distance" value
  return obj1;
}
Surface sdScene(vec3 p) {
  mat3 transform1 = rotateX(iTime)*rotateY(iTime)*rotateZ(iTime);
  mat3 transform2 = rotateX(-iTime)*rotateY(-iTime)*rotateZ(-iTime);
  vec3 lift = vec3(0., -4., 0.);
  vec3 clr = 0.5 + 0.5 * cos(iTime + vec3(0,2,4));
  float distort = cos(5. * p.x) * cos(5. * p.y) * cos(5. * p.z) * 0.2;
  Surface sphere = sdSphere(p + lift, .7, vec3(0.0), clr);
  Surface boxFrame = sdBoxFrame(p + lift, vec3(1.), 0.12, clr / 2., transform1);
  Surface boxFrame2 = sdBoxFrame(p + lift, vec3(2.), 0.12, clr / 2.5, transform2);
  Surface boxFrame3 = sdBoxFrame(p + lift, vec3(3.), 0.12, clr / 3., transform1);
  vec3 floorColor = vec3(1. + 0.7*mod(floor(p.x) + floor(p.z), 2.0));

  Surface a = minWithColor(sphere, boxFrame);
  Surface a2 = minWithColor(a, boxFrame2);
  Surface a3 = minWithColor(a2, boxFrame3);
  Surface co = minWithColor(a3, sdFloor(p, floorColor));
  return co;
}
Surface rayMarch(vec3 ro, vec3 rd, float start, float end) {
  float depth = start;
  Surface co;

  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    co = sdScene(p);
    depth += co.sd;
    if (co.sd < PRECISION || depth > end) break;
  }

  co.sd = depth;

  return co;
}

vec3 calcNormal(in vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.0005;
    return normalize(
      e.xyy * sdScene(p + e.xyy).sd +
      e.yyx * sdScene(p + e.yyx).sd +
      e.yxy * sdScene(p + e.yxy).sd +
      e.xxx * sdScene(p + e.xxx).sd);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

  vec3 col = vec3(0);
  vec3 ro = vec3(0, 5, 10);
  vec3 rd = normalize(vec3(uv.x, uv.y - 0.2, -1));

  Surface co = rayMarch(ro, rd, MIN_DIST, MAX_DIST);

  if (co.sd > MAX_DIST) {
    col = vec3(0.835, 1, 1);
  } else {
    vec3 p = ro + rd * co.sd;
    vec3 normal = calcNormal(p);
    vec3 lightPosition = vec3(2, 5, 15);
    vec3 lightDirection = normalize(lightPosition - p);

    float dif = clamp(dot(normal, lightDirection), 0.3, 1.);
    col = dif * co.col + vec3(0.835, 1, 1) * .2;
  }

  fragColor = vec4(col, 1.0);
}
