#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h/2. + vec3(1,2,3)   )*.6 + .5 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, R.y))*200.;

    float sc,dotp,totdist=0., tt=iTime/3., t=iTime;

    float param = -4.;
    for (float i=0.; i<60.; i++) {

        vec4 p = vec4( rd*totdist)*15.;

        if ( mod(t,10.)>5.) param = -3.;
        p.xyz += vec3(0,0,-4); //param); 

        p.xz *= rot( .7 ) ; //tt/5. );

        p.yzw = p.xyz; 

        sc = 1.; 

        p.zw *= rot( tt );

        p.xw *= rot( tt*1.3);

        p.yw *= rot( tt*2.);

        vec4 w = p;

        for (float j=0.; j<7.; j++) {

            p = abs(p);
            p = sqrt ( p.xyzw * p.yzwx ) *.8;

            dotp = max(1./dot(w,w),.01);
            sc *= dotp; 

            p = p * dotp - .51; 

            w = log(1.+log(1.+log(1.+p)*p)*p) - .25; 


        }

        float dist = length(p)/sc ;

        float stepsize = dist/100. ;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count


        c +=
             .2 * 
             + H(M(sc)) * exp(-i*i*stepsize*stepsize*4e7);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
