void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float a = atan(p.x,p.y)/3.1416;
    float r = length(p);
    float h = abs(a);
    float d = (1.0-r)*(1.0-smoothstep(0.0,0.5,h));
    vec3 col = vec3(d);
    fragColor = vec4(col,1.0);
}
