float v(vec2 p) {
    vec2 f = floor(p),
        q = p - f;
    ivec4 r = ivec4(f, ++f);
    vec4 h = vec4((r.xzxz * 967 ^ r.yyww * 907) % 31),
        o = vec4(q, --q),
        s = sin(h) * .3 - o.xzxz,
        c = cos(h) * .3 - o.yyww,
        d = s * s + c * c;
    return sqrt(min(min(d.x, d.y), min(d.z, d.w)));
}

void mainImage(out vec4 color, in vec2 uv) {
    float dist = v(uv * vec2(0.02, 0.01) + iTime * vec2(0.5, -0.3));
    dist += v(uv * vec2(0.01, 0.02) + iTime * vec2(-0.3, 0.5));
    color = vec4(mix(vec3(0.1, 0.25, 0.35), vec3(0.7, 0.7, 1.0), pow(dist, 4.0) * 0.15), 1.0);
    // color = vec4(vec3(v(uv * .05)), 1);
}
