#define PI 3.141592652589
#define p(a) length(vec2(cos(a),sin(a))-uv)
void mainImage( out vec4 fragColor, in vec2 c )
{
    vec2 uv = (c * 2. - iResolution.xy) / iResolution.y;
    float t = .5*iTime;

    vec3 a = vec3(0., 2., 4.) * PI / 3. + 0.5 * t;
    vec3 d = - vec3(p(a.x), p(a.y), p(a.z));
    d = sin(d * 20. + vec3(3,4,5) * t) * 0.49 + 0.5;

    vec3 col = fract(1.5*sin(0.8*t)*d.xyz + (0.7*sin(.1*t)+1.1)*d.yzx + cos(t)*d.zxy);
    fragColor = vec4(col,1.0);
}
