#define OPTION 3

float Scanlines(float x, float repeat, float modValue)
{
    x = floor(x * repeat);
    return mod(x, modValue);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalised pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    if(0 == OPTION)
    {
        float repeat = 10.0;
        float modValue = 2.0;
        float scanlines = Scanlines(uv.y + iTime * 0.1, repeat, modValue);

        fragColor = vec4(vec3(scanlines), 1.0);
    }
    else if(1 == OPTION)
    {
        float repeat = 10.0;
        float modValue = 2.0;
        float scanlines = Scanlines(uv.y + iTime * 0.1, repeat, modValue);

        vec3 red = vec3(0.85, 0.2, 0.2);
        vec3 blue = vec3(0.2, 0.2, 0.85);
        fragColor.rgb = mix(red, blue, scanlines);
    }
    else if(2 == OPTION)
    {
        float repeat = 20.0;
        float modValue = 6.0;
        float scanlines = Scanlines(uv.y + iTime * 0.1, repeat, modValue);

        vec3 tint = vec3(0.2, 0.85, 0.2);
        fragColor = vec4(vec3(scanlines) * tint, 1.0);
    }
    else if(3 == OPTION)
    {
        float repeat = 100.0;
        float modValue = 2.0;
        float scanlines = Scanlines(uv.y + iTime * 0.1, repeat, modValue);

        fragColor = texture(iChannel0, uv);
        fragColor.rgb = mix(fragColor.rgb, vec3(scanlines), 0.1);
    }
    else if(4 == OPTION)
    {
        float repeat = 8.0;
        float scanlines = fract(uv.y * repeat + iTime);
        fragColor = vec4(vec3(scanlines), 1.0);
    }
    else if(5 == OPTION)
    {
        float repeat = 8.0;
        float scanlines = fract(uv.y * repeat + iTime);

        vec3 tint = vec3(0.78, 0.32, 1.0);
        fragColor = vec4(vec3(scanlines * tint), 1.0);
    }
}
