void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

#define P(x,y) neighborhood+=S(x,y)
#define S(x,y) int(texelFetch(iChannel0,ivec2(fragCoord)+ivec2(x-int(iFrame%2==0),y),0).r)
#define R(x) if(neighborhood==x){color=1.;}
//from https://www.shadertoy.com/view/WttXWX
float random(uint x)
{
    x ^= x >> 16;
    x *= 0x7feb352dU;
    x ^= x >> 15;
    x *= 0x846ca68bU;
    x ^= x >> 16;
    return float(x)/float(0xffffffffU);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iFrame%int(iResolution.x*2.)==0) {
     if(fragCoord.x>iResolution.x/2.) {
      fragColor = vec4(vec3(step(0.5,random(uint(float(iFrame)*(iResolution.x/2.)*iResolution.y/(iResolution.x*2.)+fragCoord.x+fragCoord.y*(iResolution.x/2.))))),1.);
      return;
     } else {
      fragColor = vec4(vec3(S(0,0)|S(int(iResolution.x/2.),0)),0.);
      return;
     }
    }
    int neighborhood = 0;
    P(-1,-1);P(0,-1);P(1,-1);
    P(-1,0);         P(1,0);
    P(-1,1); P(0,1); P(1,1);
    float color = 0.;
    if(S(0,0)==0) {
      R(3)
    } else {
      R(2) else R(3)
    }
    fragColor = vec4(vec3(color),1.0);
}
