void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (5.0+mod(iTime, 5.0)) * cos(sin(tan(fragCoord/iResolution.xy)));

    vec2 uv2 = (iTime / cos(sin(uv))*(iTime+fragCoord.x)) - (iTime / cos(sin(uv))*0.5)* (iTime + cos(fragCoord.x) * 0.5) - (iTime + cos(fragCoord.y) * 0.5 );

    float c=length(uv);
    float d=length(uv.x-uv2.y / ((15.0/ iResolution.x) * (30.0 / iResolution.x)) );

    uv2=(uv+(atan(d+uv2.y+cos(iTime)) + atan(d+uv2.x+sin(iTime))));


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(c- iTime+uv2.xyx+vec3(0.0+c,2.0*c+iTime,4.0/c));


    // Output to screen
    fragColor =vec4(tan(col)/tan(500.0+iTime*(1.0+(mod(1.0,iTime)))),0.5/sin(uv2.x));


}
