void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float RADIUS = 20.0*exp(sin(iTime));
    float SHARPNESS = (uv.x<0.5) ? 0.0: 1.0;

    vec4 tex0 = texture(iChannel0, uv);
    vec4 tex1 = texture(iChannel0, uv, log2(RADIUS));

    fragColor = mix(tex0, tex1, -SHARPNESS);
    fragColor.rgb *= min(abs(fragCoord.x-0.5*iResolution.x), 1.0);
}
