vec3 palette(float t) {
    vec3 a = vec3(1.000,1.000,1.000);
    vec3 b = vec3(0.278,0.278,0.278);
    vec3 c = vec3(0.757,0.051,0.773);
    vec3 d = vec3(1.000,1.000,1.000);

    return a + b*cos(6.28318*(c*t+d) );
}

float getAudioKick() {
    return texture(iChannel0, vec2(0.1, 0.0)).x; // Low frequencies
}

float getAudioSnare() {
    return texture(iChannel0, vec2(0.2, 0.0)).x; // Mid frequencies
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y; 
    vec2 uv0 = uv;

    vec3 finalColor = vec3(0.0);

    // Get audio data
    float kickIntensity = getAudioKick();
    float snareIntensity = getAudioSnare();
    float beatIntensity = max(kickIntensity, snareIntensity);

    // Beat detection threshold
    float beatThreshold = .5; // Adjust this value as needed

    // Number of fractal iterations based on beat
    float iterations = 3.0 + 6.5 * step(beatThreshold, beatIntensity);

    for(float i = 0.0; i < iterations; i++){
        // Use beat intensity to influence the fractal
        float beatEffect = 1.0 + beatIntensity * 70.0;
        uv = fract(uv * .028979 * beatEffect) - 0.5;

        float d = length(uv);
        vec3 col = palette(length(uv0) + beatIntensity);
        d = abs(d);
        d = .04 / d;
        finalColor += col * d;
    }

    // Add vignette effect
    vec2 vignetteUV = fragCoord / iResolution.xy;
    vignetteUV = (vignetteUV - 0.5) * 2.0;
    float vignette = 0.85 - dot(vignetteUV, vignetteUV);
    vignette = pow(vignette, 2.9);

    finalColor *= vignette;

    // Intensify colors based on beat
    finalColor *= 1.0 + beatIntensity;

    fragColor = vec4(finalColor, 1.0);
}
