// Complex Collatz By Rik Riesmeijer, 2024 - No rights reserved.
void mainImage(out vec4 c, vec2 v) {
    c.ba = v / iResolution.xy * 3.3;
    c.rg = fract(0.5 * ceil(c.b + c.a)) > 0.0? c.ba + c.ba + c.ba + fract(iTime): c.ba - abs(c.b - c.a);
    c.ba = fract(0.5 * ceil(c.r + c.g)) > 0.0? c.rg + c.rg + c.rg + fract(iTime): c.rg - abs(c.r - c.g);
    c.rg = fract(0.5 * ceil(c.b + c.a)) > 0.0? c.ba + c.ba + c.ba + fract(iTime): c.ba - abs(c.b - c.a);
    c.ba = fract(0.5 * ceil(c.r + c.g)) > 0.0? c.rg + c.rg + c.rg + fract(iTime): c.rg - abs(c.r - c.g);
    c = fract(c);
}
