void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-998.9*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.980,0.000,0.718);

    float m = .0;
    float t = iTime*.17;
    float dist = length(uv);

    // Get audio data from the microphone
    float audioLevel = texture(iChannel0, vec2(0.1, 0.0)).x;

    // Amplify the audio level for more noticeable effect
    audioLevel *= 2.5;

    vec2 gv = fract( uv*.05*(30000.) ) + sin(uv);

    for (float y=-1.; y<=1.; y++){
        for (float x=-1.; x<=1.; x++){
            // Use audioLevel in the offset calculation
            vec2 offs = vec2(sin(iTime), y + audioLevel);

            float d = length(gv-offs);

            float r = mix(.025, 3., sin(t+dist)*.5+.5);
            m += smoothstep(r, r/7., d);
        }
    }

    col += mod(m, 2.);

    // Add a pulsing effect based on the audio level
    col *= 1.0 + audioLevel * 0.2;

    fragColor = vec4(col,1.0);
}
