void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float t = iTime * 0.5;
    vec3 color = vec3(0.0);

    // points
    for(float i = 0.0; i < 100.0; i++) {
        // point position
        vec2 pos = vec2(
            sin(i * 0.043 + t) * 0.2,
            cos(i * 0.033 - t) * 0.2
        );

        // pixel to point distance
        float d = length(uv - pos);

        // glow
        color += 0.00025 / (d * d);
    }

    // center point
    float centralGlow = 0.02 / length(uv);
    color += vec3(1.0, 0.7, 0.3) * centralGlow;

    // color variation
    color.r += sin(t) * 0.2;
    color.g += cos(t * 1.1) * 0.2;
    color.b += sin(t * 0.9) * 0.2;

    // background gradient
    color += vec3(0.1, 0.05, 0.2) * (1.0 - length(uv));

    fragColor = vec4(color, 1.0);
}
