#define PI 3.14159
#define R iResolution

const vec3
black=vec3(0.),
white=vec3(1.),
A=vec3(.5),
B=vec3(.5),
C=vec3(1.),
D=vec3(0,.2,.4);
vec3 palette(float t){return A+B*cos(2.*PI*(C*t+D));}

mat2 rot2D(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}

float gear(vec2 uv) {
    float N,d,a,r,v,da,s;
    N=10.;d=length(uv);a=atan(uv.y,uv.x);
    s=sin(a*N);
    r=ceil(1.+s)/2.+1.;
    v=1.-step(.02,abs(d-r));

    r=step(1.5,d)*(1.-step(2.,d));
    da=1.-step(.1,abs(s));
    v=max(v,r*da);

    return v;
}

void mainImage(out vec4 C, in vec2 I) {
    vec2 uv = (2.*I-R.xy)/R.y;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float v = gear((uv*5.5-.5)*rot2D(iTime));
    v=max(v,gear((uv*5.5+2.1)*rot2D(.95)*rot2D(-iTime)));
    col = mix(black, col, v);
    C = vec4(col,1.0);
}
