const int MAX_ITERATIONS = 22;
const float MAX_FRACTION = float(MAX_ITERATIONS);

float burningShip(in vec2 coord) {
    vec2 z = coord;
    if(coord.y < 0.0) {
        coord.y += sin(iTime + sin(coord.x*10.0 + iTime)*2.5)*0.001*(1.0-smoothstep(-0.09,0.0,coord.y));
    }
    for(int i = 1; i < MAX_ITERATIONS; ++i) {
        z = abs(z);
        z = vec2(z.x*z.x - z.y*z.y, z.x*z.y*2.10) + coord;
        if(length(z) > 2.0)
            return float(i) / MAX_FRACTION + smoothstep(2.5,5.5,length(z)) / MAX_FRACTION;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 coord = fragCoord / iResolution.xy;
    coord = coord * 0.10 - vec2(1.805,0.008);
    coord.y = -coord.y * 0.75;
    float val = burningShip(coord);
    val = sqrt(val);
    vec3 color = mix(vec3(0.15),vec3(0.7,0.7,0.1),val);
    color = mix(color, vec3(0.9,0.3,0.25), 2.0 * val - 0.5);
    fragColor = vec4(color,1.0);
}
