vec3 krug(vec2 uv, float r){
    float q = sin(iTime)*7.;
    q = atan(q*8.)/8.;
    q = abs(q);
    q = smoothstep(0., 0.1, q);

    float w = sin(iTime*iTime)*4.;
    w = cos(w*8.)/8.;
    w = abs(w);
    w = smoothstep(0., 0.1, w);

    float d = length(uv) - r;
    d = sin(d*8.)/8.;
    d = abs(d);
    d = smoothstep(0., 0.1, d);

    return vec3(q/d, 3.*d/q, w/q);
} 


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv - .5) * 2.;
    uv.x  = uv.x * iResolution.x/iResolution.y;
    vec3 col = krug(uv, iTime*iTime);
    fragColor = vec4(col,1.0);
}
