 highp float rand( const in vec2 uv ) {
       const highp float a = 12.9898, b = 78.233, c = 43758.5453;
        highp float dt = dot( uv.xy, vec2( a,b ) ), sn = mod( dt, 3.1415926538);
        return fract( sin( sn ) * c );
 }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    float xAmp = .15;
    float scanStrength = .15;
    float xSpeed = .222;
    float noiseStrength = .1;

    vec2 uv = fragCoord/iResolution.xy;
    vec4 vid = texture(iChannel0, uv);
    vec2 vUv =  (uv - .5) * 2.0;

    uv.x += sin(uv.y * 10.0 + iTime * 3.0) * 0.02;

    vec3 noise = vec3(rand(uv * (1.0 + iTime)));
    float y = uv.y;
    y += (10.0 * iTime * -.025);
    float count = 400.0;
	float scanline = sin(y*count + sin(uv.x *10. + iTime * -10. * xSpeed) * 100. * xAmp);// + sin(uv.x * count);

    float a = smoothstep(0.01, 0.1, uv.x);
    smoothstep(1., 0.9, uv.x) *
    smoothstep(0.01, 0.1, uv.y) *
    smoothstep(1., 0.9, uv.y);

    fragColor = vec4(vid.xyz * a * noise  +  scanline * scanStrength, a);
}
