// Radius and tolerance adjustable in Common tab
// Left: separable
// Right: non-separable

void mainImage( out vec4 C, in vec2 U ){
    vec2 u = U/iResolution.xy;
    //C -= C;
    float offs = 0.5;

    if(iMouse.z > 0.0){
        offs = iMouse.x/iResolution.x;
    }

    if(u.x > offs){
        C.rgb = do_bilateral_pass(u, iChannel0, false, iResolution.xy, 0);
    } else {
        C.rgb = texture(iChannel1,u).rgb;
    }
    C -= step(abs(u.x - offs),1./iResolution.x)*0.07;
}

#define rad 10.
#define tolerance 0.1

float luma(vec3 col) {
    return dot(col, vec3(0.2126729, 0.7151522, 0.0721750));
}



void do_samp(vec2 u, sampler2D chan, inout float W, inout vec3 acc, float cent_lum){
    float w = 1.0;
    vec3 samp = texture(chan,u).rgb;
    float samp_lum = luma(samp);
    w *= 1.-smoothstep(0.,tolerance,abs(samp_lum - cent_lum));
    acc += samp * w;
    W += w;
}

vec3 do_bilateral_pass(vec2 u, sampler2D chan, bool separable, vec2 res, int iter){
    vec3 C = vec3(0);
    C = texture(chan,u).rgb;
    float cent_lum = luma(C.rgb);

    vec2 st = 1./res.xy;
    vec3 acc = vec3(0);
    float W = 0.;
    if(separable){
        vec2 dir = vec2(0);
        dir[iter] = 1.;
        for(float x = -rad; x <= rad; x++){
            do_samp(u + x*dir*st, chan, W, acc, cent_lum);

        }
    } else {
        for(float y = -rad; y <= rad; y++){
            for(float x = -rad; x <= rad; x++){
                do_samp(u + vec2(x,y)*st, chan, W, acc, cent_lum);

            }
        }
    }
    if(W > 0.0001){
        C.rgb = acc/W;
    }
    return C;
}

void mainImage( out vec4 C, in vec2 U ){
    vec2 u = U/iResolution.xy;
    C = texture(iChannel0,u);
}

void mainImage( out vec4 C, in vec2 U ){
    vec2 u = U/iResolution.xy;    
    C.rgb = do_bilateral_pass(u, iChannel0, true, iResolution.xy, 0);
}

void mainImage( out vec4 C, in vec2 U ){
    vec2 u = U/iResolution.xy;    
    C.rgb = do_bilateral_pass(u, iChannel0, true, iResolution.xy, 1);
}
