void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 bgColor = vec3(1.0);  // White background
    float gridSize = 20.0, lineWidth = 1.0, zoom = 12.0;
    float aspect = iResolution.x / iResolution.y;
    vec2 uv = fragCoord.xy / zoom, correctedGridSize = vec2(gridSize * aspect, gridSize);
    float t = iTime * 0.1, freq = 0.1, amp = 5.0;

    uv += vec2(
        sin(uv.y * freq + t) * amp + sin(uv.x * freq * 1.5 + t * 1.2) * amp * 0.8 + cos((uv.x + uv.y) * freq * 0.5 + t * 0.5) * amp * 0.6,
        sin(uv.x * freq + t) * amp + sin(uv.y * freq * 1.5 + t * 1.2) * amp * 0.8 + cos((uv.x + uv.y) * freq * 0.5 + t * 0.5) * amp * 0.6
    );

    vec2 gridCoord = mod(uv, iResolution.xy / correctedGridSize);
    float lineDist = min(min(gridCoord.x, (iResolution.x / correctedGridSize.x) - gridCoord.x), min(gridCoord.y, (iResolution.y / correctedGridSize.y) - gridCoord.y));
    vec3 finalColor = mix(bgColor, vec3(0.0, 0.0, 1.0), smoothstep(0.0, lineWidth, lineDist));  // Blue grid lines

    fragColor = vec4(finalColor, 1.0);
}
