void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float spacing = 10.0;
    float dotRadius = 4.0;

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 videoColor = texture(iChannel0, uv);


    vec2 pos = mod(fragCoord.xy, spacing);
    float dist = length(pos - spacing / 2.0);
    float c = smoothstep(dotRadius - 1.0, dotRadius, dist);
    fragColor = vec4( mix(vec3(c), videoColor.rgb, c), 1.0);
}
