#define RAYMARCH_TIME 128
#define PRECISION .001
#define AA 5
#define PI 3.14159265

// https://www.shadertoy.com/view/NdjcWh
vec2 fixUV(in vec2 c) {
    return (2. * c - iResolution.xy) / min(iResolution.x, iResolution.y);
}

mat2 rot(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);
}

float cap(vec3 p, float l, float w)
{
    return length(p-vec3(0., clamp(p.y, -l, l), 0.)) - w;
}

float torus(vec3 p, float r, float w)
{
    return sqrt(pow(length(p.xz)-r, 2.) + pow(p.y, 2.)) - w;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
    vec3 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h) - r;
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb) {
    p.x = abs(p.x);
    float k = (sc.y * p.x > sc.x * p.y) ? dot(p.xy, sc) : length(p.xy);
    return sqrt(dot(p, p) + ra * ra - 2.0 * ra * k) - rb;
}

vec4 opElongate(in vec3 p, in vec3 h) {
    vec3 q = abs(p) - h;
    return vec4(max(q, 0.0), min(max(q.x, max(q.y, q.z)), 0.0));
}

vec2 opU(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

vec2 map(in vec3 p) {
    //p.xz *= rot(iTime*.5);
    p -= vec3(0., 1.5, 3.);
    //vec3 id = floor(p);

    //p = mod(p+vec3(11.), 20.)-vec3(11.);

    vec3 px = vec3(abs(p.x), p.y, p.z);

    float barrel = max(max(cap(p.xzy, 1., .1), -cap(p.xzy, 2., .05)), p.z-.5);
    vec3 hfp = px*13.;
    hfp = mod(hfp+vec3(.5), 2.)-vec3(.5);
    float holes = (length(hfp)-1.)/13.;
    float grid = max(max(max(cap(p.xzy, 1., .18), -cap(p.xzy, 2., .16)), p.z-.4), -holes);
    barrel = min(barrel, grid);

    // Body
    vec3 bp = px.xzy;
    bp.z *= .4;
    float body = max(max(cap(bp-vec3(0., -.1, -.1), .1, .1), (p.zy*rot(-.5)).x-.1), px.x-.15);
    float bigBody = cap(bp-vec3(0., -3., -.2), 2.5, .4);
    float frontHole = cap(px.xzy-vec3(0., -1.5, -.8), .6, .5);
    float backHole = cap(px.xzy-vec3(0., -4., -.8), .6, .5);
    float topRidge = cap(px.xzy-vec3(0., -1.4, .49), 5., .08);
    float sideRidge = cap(px.xzy-vec3(0.2, -1.8, 0.), 5., .03);
    vec3 fdp = px;
    fdp.xz *= rot(-.5);
    float fing1 = cap(fdp.yxz-vec3(-.7, -1.8, -0.01), 3., .13);
    float fing2 = cap(fdp.yxz-vec3(-.9, -1.8, -0.05), 3., .13);
    float fing3 = cap(fdp.yxz-vec3(-1.1, -1.8, -0.1), 3., .13);
    float fing4 = cap(fdp.yxz-vec3(-1.3, -1.8, -0.15), 3., .13);
    float t = max(torus(p-vec3(0., -.9, -2.8), .65, .48), -p.y-1.25);
    body = max(max(max(max(max(max(max(max(max(max(max(min(body, bigBody), (px.x-.21)), -topRidge), -sideRidge), -frontHole), -backHole), -fing1), -fing2), -fing3), -fing4), -t), -p.z-5.75);

    // Top thing
    vec3 hp = px-vec3(0., .3, -3.6);
    float handle = cap(hp.xzy, 2., .5);
    float handleIndent = cap(px.xzy-vec3(0., -3.3, .95), 2., .5);
    float inIndent = cap(px.xzy-vec3(0., -3.4, .85), 3., .3);
    float bigInd = cap(px.xzy-vec3(0.4, -4., 0.18), 1.5, .25);
    float backInd = cap(px-vec3(0., 1.6, -6.05), 1.5, .2);
    float tube = cap(px-vec3(0.2, .2, -3.), .2, .05);
    vec3 wp = px - vec3(0.25, 0., 1.);
    wp *= 10.;
    wp.z = mod(wp.z+2., 1.)-2.;
    float wiggles = cap(wp-vec3(0., 5.5, -1.5), .25, .5)/10.;
    handle = min(max(max(max(max(max(max(max(handle, hp.x-.25), -handleIndent), -inIndent), -wiggles), -bigInd), -p.z-5.9), -backInd), tube);

    // Trigger
    vec3 tp = px;
    float trigger = cap(px.xzy-vec3(0., -2.4, -.55), .2, .2);
    float curve = cap(px.yxz-vec3(-.4, 0., -1.8), .2, .5);
    float curve2 = cap(px.yxz-vec3(-.47, 0., -2.58), .2, .15);
    float curve3 = cap(px.yxz-vec3(-.53, 0., -2.15), .2, .15);
    trigger = max(max(max(max(trigger, -curve), px.x-.05), -curve2), -curve3);

    // Switch
    float swit = length(p-vec3(0.2, -.4, -2.8)) -.06;

    float gun = min(min(min(min(body, barrel), handle), trigger), swit);
    // Bullet added by scratch13764
    float bullet = length(p - vec3(0,0,fract(iTime)*10.))-.07; 
    vec2 d = vec2(min(gun , bullet), 2.);
    return d;
}

vec2 rayMarch(in vec3 ro, in vec3 rd) {
    float t = 0.1;
    float tmax = 40.;
    vec2 res = vec2(-1.);
    if(rd.y < 0.) {
        float tp = -ro.y / rd.y;
        tmax = min(tmax, tp);
        res = vec2(tp, 1.);
    }
    for(int i = 0; i < RAYMARCH_TIME && t < tmax; i++) {
        vec3 p = ro + t * rd;
        vec2 d = map(p);
        if(d.x < PRECISION) {
            res = vec2(t, d.y);
            break;
        }
        t += d.x;
    }
    return res;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1, -1);
    return normalize(k.xyy * map(p + k.xyy * h).x +
        k.yyx * map(p + k.yyx * h).x +
        k.yxy * map(p + k.yxy * h).x +
        k.xxx * map(p + k.xxx * h).x);
}

mat3 setCamera(vec3 ta, vec3 ro, float cr) {
    vec3 z = normalize(ta - ro);
    vec3 cp = vec3(sin(cr), cos(cr), 0.);
    vec3 x = normalize(cross(z, cp));
    vec3 y = cross(x, z);
    return mat3(x, y, z);
}

// https://iquilezles.org/articles/rmshadows
float softShadow(in vec3 ro, in vec3 rd, float k) {
    float res = 1.0;
    float ph = 1e20;
    float tmin = .1;
    float tmax = 10.;
    for(float t = tmin; t < tmax;) {
        float h = map(ro + rd * t).x;
        if(h < 0.001)
            return 0.0;
        float y = h * h / (2.0 * ph);
        float d = sqrt(h * h - y * y);
        res = min(res, k * d / max(0.0, t - y));
        ph = h;
        t += h;
    }
    return res;
}

// https://iquilezles.org/articles/checkerfiltering
float checkersGrad(in vec2 uv, in vec2 ddx, in vec2 ddy) {
    vec2 w = max(abs(ddx), abs(ddy)) + 0.01;
    vec2 i = (2. * abs(fract(.5 * (uv - 0.5 * w)) - .5) - (2. * abs(fract(.5 * (uv + 0.5 * w)) - .5))) / w;
    return 0.5 - 0.5 * i.x * i.y;
}

vec3 render(in vec2 uv, in vec2 px, in vec2 py) {
    float ro_an = .3 * sin(.3 * iTime);
    vec3 ro = vec3(4. * cos(ro_an), 1. + .5 * sin(.3 * iTime), 4. * sin(ro_an));
    if(iMouse.z > 0.01) {
        float theta = iMouse.x / iResolution.x * 2. * PI;
        ro = vec3(4. * cos(theta), 2. - iMouse.y / iResolution.y * 2., 4. * sin(theta));
    }
    vec3 ta = vec3(0., 1., 0.);
    mat3 cam = setCamera(ta, ro, 0.);
    float fl = 1.;
    vec3 rd = normalize(cam * vec3(uv, fl));
    vec3 bg = vec3(.7, .7, .9);
    vec3 color = bg - rd.y * vec3(.1);
    vec2 t = rayMarch(ro, rd);
    if(t.y > 0.) {
        vec3 p = ro + t.x * rd;
        vec3 n = (t.y < 1.1) ? vec3(0., 1., 0.) : calcNormal(p);
        vec3 light = vec3(6., 4., 5.);
        float dif = clamp(dot(normalize(light - p), n), 0., 1.);
        p += PRECISION * n;
        dif *= softShadow(p, normalize(light - p), 10.);
        float amb = 0.5 + 0.5 * dot(n, vec3(0., 1., 0.));
        vec3 c = vec3(0.);
        if(t.y > 1.9 && t.y < 2.1) {
            // Body
            c = vec3(1.);
        } else if(t.y > 2.9 && t.y < 3.1) {
            // Black
            c = vec3(0.);
        } else if(t.y > 0.9 && t.y < 1.1) {
            // plane
            vec3 rdx = normalize(cam * vec3(px, fl));
            vec3 rdy = normalize(cam * vec3(py, fl));
            vec3 ddx = ro.y * (rd / rd.y - rdx / rdx.y);
            vec3 ddy = ro.y * (rd / rd.y - rdy / rdy.y);
            c = vec3(.1) + vec3(0.1) * checkersGrad(p.xz, ddx.xz, ddy.xz);
        }
        color = amb * c + dif * vec3(.7);
    }
    return sqrt(color);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 color = vec3(0.);
    for(int m = 0; m < AA; m++) {
        for(int n = 0; n < AA; n++) {
            vec2 offset = 2. * (vec2(float(m), float(n)) / float(AA) - .5);
            vec2 uv = fixUV(fragCoord + offset);
            vec2 px = fixUV(fragCoord + vec2(1., 0.) + offset);
            vec2 py = fixUV(fragCoord + vec2(0., 1.) + offset);
            color += render(uv, px, py);
        }
    }
    fragColor = vec4(color / float(AA * AA), 1.);
}
