#define rot(a) mat2(cos(a + vec4(0, 33, 11, 0)))
void mainImage(out vec4 o, vec2 u) { 
    o *= 0.;
    vec2 r = iResolution.xy;
    u = (u + u - r) / r.y;
    float s, c, i, a, h, d = 1., t = iTime; 

    while(i++ < 120. && d > .1) {
        vec3 p = vec3(a * u, a - 5.);

        p.xz *= rot(-1.6 * cos(t) + .8);
        p.y += cos(4.* t) * .8;

        p *= .5;

        h = mix(21. / dot(p, p), 1., sin(t * .5) * .5 + .5);

        p *= h;

        c = cos(p.y * 2.);
        s = sin(p.x + p.z) * .07 - .02;

        d = sin(s) * c * 5. + .5;
        a += d / h; 
   }
   o += vec4(8, 12, 18, 0) / i;
}
