void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime * 0.5;
    vec3 color = 0.4 + 0.4 * sin(t + uv.xyx * 3.0 + vec3(0, 2, 4));

    color += fract(sin(dot(uv * 150.0, vec2(12.9898, 78.233))) * 43758.5453) * 0.05;

    float wave = sin(uv.x * 8.0 + t * 2.0) * sin(uv.y * 8.0 + t * 2.0);
    uv += wave * 0.02;

    vec3 light = vec3(1.0) / (length(uv - 0.5) * length(uv - 0.5) + 0.3);
    color += light * 0.05;

    vec2 blurDir = vec2(sin(t * 0.3), cos(t * 0.3)) * 0.003;
    for (int i = 0; i < 4; i++) color = mix(color, texture(iChannel0, uv += blurDir).rgb, 0.06);

    fragColor = vec4(color, 1.0);
}
